import React from 'react';
import { useFormik } from 'formik';
import * as Yup from "yup";
import Profile from './Profile';

const Signup = () => {
    const formik = useFormik({
        initialValues: {
            firstName: "",
            lastName: "",
            email: "",
            password: "",
            favSeason: "",
        },

        validationSchema: Yup.object({
            firstName: Yup.string().required("Required").matches(/^[a-zA-Z]+$/, "Alphabets only"),
            lastName: Yup.string().required("Required").matches(/^[a-zA-Z]+$/, "Alphabets only"),
            email: Yup.string().email("Invalid email address").required(),
            password: Yup.string().required("Required").matches(/^(?=.*[A-Z])(?=.*\d)(?=.*[$@$!%*?#&])[A-Za-z\d$@$!%#*?&]/, "Must contain at least 1 Alphabet, 1 Number, 1 Special Character and 1 Upper case letter"),
        }),

        onSubmit: (values) => {
            console.log(values);
        }
    });

    return (
        <form onSubmit={formik.handleSubmit}>
            <div className="container">
                <div className="row">
                    {/*first name*/}
                    <input className="col-md-6"
                        id="firstName"
                        name="firstName"
                        type="text"
                        placeholder="First Name"
                        onChange={formik.handleChange}
                        onBlur={formik.handleBlur}
                        value={formik.values.firstName} />

                    {/*last name*/}
                    <input className="col-md-6"
                        id="lastName"
                        name="lastName"
                        type="text"
                        placeholder="Last Name"
                        onChange={formik.handleChange}
                        onBlur={formik.handleBlur}
                        value={formik.values.lastName} />

                    {/*email*/}
                    <input className="col-md-6"
                        id="email"
                        name="email"
                        type="email"
                        placeholder="email"
                        onChange={formik.handleChange}
                        onBlur={formik.handleBlur}
                        value={formik.values.email} />

                    {/*password*/}
                    <input className="col-md-6"
                        id="password"
                        name="password"
                        type="password"
                        placeholder="password"
                        onChange={formik.handleChange}
                        onBlur={formik.handleBlur}
                        value={formik.values.password} />

                    {/*favourite season*/}
                    <select className="col-md-6"
                        id="favSeason"
                        name="favSeason"
                        onChange={formik.handleChange}
                        value={formik.values.favSeason}
                        onBlur={formik.handleBlur}
                    >
                        <option value="" label="Select a season" />
                        <option value="Spring" label="Spring" />
                        <option value="Summer" label="Summer" />
                        <option value="Fall" label="Fall" />
                        <option value="Winter" label="Winter" />
                    </select>
                </div>

                {/*display error message*/}
                <div className="col-md-6" id="errorMsg">
                    {formik.touched.firstName && formik.errors.firstName ? <p>{formik.errors.firstName}</p> : null}
                    {formik.touched.lastName && formik.errors.lastName ? <p>{formik.errors.lastName}</p> : null}
                    {formik.touched.email && formik.errors.email ? <p>{formik.errors.email}</p> : null}
                    {formik.touched.password && formik.errors.password ? <p>{formik.errors.password}</p> : null}
                </div>
            </div>
            <button type="submit">Submit</button>
        </form>
    );
}

export default Signup;
