import React, { useState } from 'react';
import { useFormik } from 'formik';
import * as Yup from "yup";
import Profile from './Profile';

const Signup = () => {
    const [isValid, setValid] = useState(false);
    const formik = useFormik({
        initialValues: {
            firstName: "",
            lastName: "",
            email: "",
            password: "",
            favSeason: "",
        },

        validationSchema: Yup.object({
            firstName: Yup.string().required("First name can't be empty!").matches(/^[a-zA-Z]+$/, "Alphabets only"),
            lastName: Yup.string().required("Last name can't be empty!").matches(/^[a-zA-Z]+$/, "Alphabets only"),
            email: Yup.string().email("Invalid email address").required("Email can't be empty!"),
            password: Yup.string().required("Password can't be empty!").matches(/^(?=.*[A-Z])(?=.*\d)(?=.*[$@$!%*?#&])[A-Za-z\d$@$!%#*?&]/, "Must contain at least 1 Alphabet, 1 Number, 1 Special Character and 1 Upper case letter"),
        }),

        onSubmit: (values) => {
            setValid(true);
        }
    });

    if (isValid) {
        /** return the Profile component with info */
        const info = {
            firstName: formik.values.firstName,
            lastName: formik.values.lastName,
            email: formik.values.email,
            favSeason: formik.values.favSeason,
        }
        return <Profile props={info}/>;
    }
    return (
        <form onSubmit={formik.handleSubmit}>
            <div className="container mt-3">
                <div className="col">
                    {/*first name*/}
                    <input className="form-control"
                        id="firstName"
                        name="firstName"
                        type="text"
                        placeholder="First Name"
                        onChange={formik.handleChange}
                        onBlur={formik.handleBlur}
                        value={formik.values.firstName} />

                    {/*last name*/}
                    <input className="form-control"
                        id="lastName"
                        name="lastName"
                        type="text"
                        placeholder="Last Name"
                        onChange={formik.handleChange}
                        onBlur={formik.handleBlur}
                        value={formik.values.lastName} />

                    {/*email*/}
                    <input className="form-control"
                        id="email"
                        name="email"
                        type="email"
                        placeholder="email"
                        onChange={formik.handleChange}
                        onBlur={formik.handleBlur}
                        value={formik.values.email} />

                    {/*password*/}
                    <input className="form-control"
                        id="password"
                        name="password"
                        type="password"
                        placeholder="password"
                        onChange={formik.handleChange}
                        onBlur={formik.handleBlur}
                        value={formik.values.password} />

                    {/*favourite season*/}
                    <select className="form-control"
                        id="favSeason"
                        name="favSeason"
                        onChange={formik.handleChange}
                        value={formik.values.favSeason}
                        onBlur={formik.handleBlur}
                    >
                        <option value="" label="Select a season" />
                        <option value="Spring" label="Spring" />
                        <option value="Summer" label="Summer" />
                        <option value="Fall" label="Fall" />
                        <option value="Winter" label="Winter" />
                    </select>
                </div>

                {/*display error message*/}
                <div className="row-md-6" id="errorMsg">
                    {formik.touched.firstName && formik.errors.firstName ? <p>{formik.errors.firstName}</p> : null}
                    {formik.touched.lastName && formik.errors.lastName ? <p>{formik.errors.lastName}</p> : null}
                    {formik.touched.email && formik.errors.email ? <p>{formik.errors.email}</p> : null}
                    {formik.touched.password && formik.errors.password ? <p>{formik.errors.password}</p> : null}
                </div>
            </div>
            <button type="submit" className="form-control">Submit</button>
        </form>
    )
}

export default Signup;
